    public int threeSumMulti(int[] A, int target) {
        if (A.length == 0) {
            return 0;
        }
        int num = 0;
        int [][] arr = new int[101][2];
        for (int i = 0; i < A.length; i++) {
            arr[A[i]][1]++;
        }

        List<Integer> lists = new ArrayList<>();
        for (int i = 0; i < A.length; i++) {
            if (!lists.contains(A[i])) {
                lists.add(A[i]);
            }
        }
        for (int i = 0; i < lists.size(); i++) {
            for (int j = i; j < lists.size(); j++) {
                for (int k = j; k < lists.size(); k++) {
                    if (lists.get(i) + lists.get(j) + lists.get(k) == target) {
                        int sum = 0;
                        //如果三个相等
                        if( i == j && j == k) {
                            int count = arr[lists.get(i)][1];
                            if (count < 3) {
                                continue;
                            } else {
                                sum = (int) (((count * (count-1)) * (count-2) / 6) % (Math.pow(10, 9)+7) );
                                /*if (count == 3) {
                                sum = 1;
                            } else {*/
                                /*int temp = 3;
                                int increTemp = 3;
                                sum = 1;
                                for (int t = 4; t <= count; t++) {
                                    sum += temp;
                                    temp += increTemp;
                                    increTemp++;
                                }*/
                            }
                            System.out.println(count+" "+sum);
                        } else if (i == j) {
                            int sumK = arr[lists.get(k)][1];
                            int sumI = 1;
                            int count = arr[lists.get(i)][1];
                            if (count < 2) {
                                continue;
                            } else if (count == 2) {
                                sumI = 1;
                            } else {
                                int temp = 2;
                                sumI = 1;
                                for (int t = 3; t <= count; t++) {
                                    sumI += temp;
                                    temp ++;
                                }
                            }
                            System.out.println(A[k]+" "+ sumK+"----"+sumI+" "+" i-j-k:"+i+j+k+" "+count);
                            sum = sumK * sumI;
                        } else if (j == k) {
                            int sumI = arr[lists.get(i)][1];
                            int sumK = 1;
                            int count = arr[lists.get(j)][1];
                            if (count < 2) {
                                continue;
                            } else if (count == 2) {
                                sumK = 1;
                            } else {
                                int temp = 2;
                                sumK = 1;
                                for (int t = 3; t <= count; t++) {
                                    sumK += temp;
                                    temp ++;
                                }
                            }
                            sum = sumK * sumI;
                        } else {
                            sum = (int) ((arr[lists.get(i)][1] * arr[lists.get(j)][1] * arr[lists.get(k)][1])
                                                                % (Math.pow(10, 9)+7));
                        }
//                        System.out.println(sum+" "+i+ " " +j +" "+k);
                        num = num + sum;
                    }
                }
            }
        }

        return num;
    }

